import React,{useState} from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import classes from "./AddUser.module.css"
const AddUser= props=>{
    const [userName,setUsername]= useState('');
    const [useAge,setUserAge]= useState('');
    const addUserHandler=(event)=>{
        event.preventDefault();
        console.log(userName,useAge);
        setUsername('');
        setUserAge('');
      };
      const userNameChangedHandler=(event)=>{
        setUsername(event.target.value);

      };
      const userAgeChangedHandler=(event)=>{
        setUserAge(event.target.value);

    };
   return( 
       <Card className={classes.input}>
   <form onSubmit={addUserHandler}>
        <label htmlFor="username">User Name</label>
        <input id="username" type="text" onChange={userNameChangedHandler}></input>
        <label htmlFor="age" >Age (year)</label>
        <input id="age" type="number" onChange={userAgeChangedHandler}></input>
        <Button type="submit">Add user</Button>
    </form>
    </Card>
    );
}
export default AddUser;
